import { useEffect, useState } from 'react';
import '../services/Api.js';
import '../styles/App.scss';



function App() {
  const [quotes, setQuotes] = useState([]);
  const [characterSearch, setCharacterSearch] = useState('');
  const [quoteSearch, setQuoteSearch] = useState('');

  useEffect(() => {
    const fetchData = () => {
      fetch('https://beta.adalab.es/curso-intensivo-fullstack-recursos/apis/quotes-friends-tv-v1/quotes.json')
        .then((response) => response.json())
        .then((data) => setQuotes(data))
        .catch((error) => console.log('Error al obtener los datos:', error));
    };

    fetchData();
  }, []);

  const handleCharacterSearch = (event) => {
    setCharacterSearch(event.target.value);
  };

  const handleQuoteSearch = (event) => {
    setQuoteSearch(event.target.value);
  };

  return (
    <div>
      <h1>Frases de Friends</h1>
      <input type="text" value={characterSearch} onChange={handleCharacterSearch} placeholder="Buscar personaje" />
      in
      <input type="text" value={quoteSearch} onChange={handleQuoteSearch} placeholder="Buscar frase" />
      <div className="box">
        {quotes.map((quote, index) => (
          <div key={index} className="box_characters">
            {quote.quote}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
