import { useEffect, useState } from 'react';
import '../services/Api.js';
import '../styles/App.scss';



function App() {
  const [quotes, setQuotes] = useState([]);
  const [characterSearch, setCharacterSearch] = useState('');
  const [quoteSearch, setQuoteSearch] = useState('');
  const [searchedCharacters, setSearchedCharacters] = useState([]);
  const [searchedQuotes, setSearchedQuotes] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      fetch('https://beta.adalab.es/curso-intensivo-fullstack-recursos/apis/quotes-friends-tv-v1/quotes.json')
        .then((response) => response.json())
        .then((data) => setQuotes(data))
        .catch((error) => console.log('Error al obtener los datos:', error));
    };

    fetchData();
  }, []);

  const handleCharacterSearch = () => {
    const foundCharacters = quotes.filter(
      (quote) => quote.character.toLowerCase().includes(characterSearch.toLowerCase())
    );
    setSearchedCharacters(foundCharacters);
  };

  const handleQuoteSearch = () => {
    const foundQuotes = quotes.filter(
      (quote) => quote.quote.toLowerCase().includes(quoteSearch.toLowerCase())
    );
    setSearchedQuotes(foundQuotes);
  };

  const handleCharacterInputChange = (event) => {
    setCharacterSearch(event.target.value);
  };

  const handleQuoteInputChange = (event) => {
    setQuoteSearch(event.target.value);
  };

  const combinedResults = [...searchedCharacters, ...searchedQuotes];

  return (
    <div>
      <h1>Frases de Friends</h1>
      <div>
        <input
          type="text"
          value={characterSearch}
          onChange={handleCharacterInputChange}
          placeholder="Buscar personaje"
        />
        <button onClick={handleCharacterSearch}>Buscar personajes</button>
      </div>
      <div>
        <input
          type="text"
          value={quoteSearch}
          onChange={handleQuoteInputChange}
          placeholder="Buscar frase"
        />
        <button onClick={handleQuoteSearch}>Buscar frases</button>
      </div>
      <div className="box">
        {combinedResults.map((quote, index) => (
          <div key={index} className="box_characters">
            {quote.quote}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
